(()=>{const s=document.getElementById("index-structure-container");if(!s)return;const d=s.dataset.indexId,e=document.getElementById("structure-output"),t=document.getElementById("refresh-structure-btn");function c(){t.classList.add("loading"),Craft.sendActionRequest("POST","search-index/indexes/structure",{data:{id:d}}).then(a=>{t.classList.remove("loading");const n=a.data;n.success?e.textContent=JSON.stringify(n.schema,null,2):e.textContent=n.message||"Failed to retrieve schema."}).catch(()=>{t.classList.remove("loading"),e.textContent="Request failed."})}t.addEventListener("click",c),c()})();
