(()=>{const d=document.getElementById("search-mode-toggle");if(!d)return;const n=d.querySelectorAll(".btn"),e=document.getElementById("single-mode"),t=document.getElementById("compare-mode");if(!(!e||!t))for(const s of n)s.addEventListener("click",()=>{for(const o of n)o.classList.remove("active");s.classList.add("active"),s.dataset.mode==="single"?(e.classList.remove("hidden"),t.classList.add("hidden")):(e.classList.add("hidden"),t.classList.remove("hidden"))})})();
